mapper.insert:
insert into JAVACODE_BOARD
(
boardnum
,title
,content

,tag


	,originalfile
	,savedfile

,updatedate
,category
)
values
(
SEQ_JAVACODE_BOARD.nextval
,#{title}
,#{content}

,#{tag}


	,#{originalfile}
	,#{savedfile}

,sysdate
,#{category}
)

mapper.update:
		update JAVACODE_BOARD set
		title = #{title}
		,content = #{content}
		
		,tag = #{tag}
		
		
			, originalfile = #{originalfile}
			, savedfile = #{savedfile}
		
		,updatedate = #{updatedate}
		,category = #{category}
		where
		boardnum = #{boardnum}
