mapper.insert:
insert into JAVACODE_BOARD
(
boardnum
,title
,content

,tag


	,originalfile
	,savedfile

,updatedate
,category
)
values
(
SEQ_JAVACODE_BOARD.nextval
,#{title}
,#{content}

,#{tag}


	,#{originalfile}
	,#{savedfile}

,sysdate
,#{category}
)

insert into JAVACODE_BOARD2
(
boardnum
,title
,content
,name


	,originalfile
	,savedfile

,updatedate
,boardType
)
values
(
SEQ_JAVACODE_BOARD2.nextval
,#{title}
,#{content}

,#{name}


	,#{originalfile}
	,#{savedfile}

,sysdate
,#{boardType}
)

mapper.update:
		update JAVACODE_BOARD set
		title = #{title}
		,content = #{content}
		
		,tag = #{tag}
		
		
			, originalfile = #{originalfile}
			, savedfile = #{savedfile}
		
		,updatedate = sysdate
		,category = #{category}
		where
		boardnum = #{boardnum}

		update JAVACODE_BOARD2 set
		title = #{title}
		,content = #{content}
		,name = #{name}
		
			, originalfile = #{originalfile}
			, savedfile = #{savedfile}
		
		,updatedate = sysdate
		,boardType = #{boardType}
		where
		boardnum = #{boardnum}
